@@ -1,1 +1,200 @@
+import 'package:flutter/material.dart';
+import 'package:intl/intl.dart';
+import '../constants/app_constants.dart';
+import '../theme/app_colors.dart';
 
+class Helpers {
+  Helpers._();
+
+  // Date Formatting
+  static String formatDate(DateTime date) {
+    return DateFormat(AppConstants.dateFormat).format(date);
+  }
+
+  static String formatTime(DateTime time) {
+    return DateFormat(AppConstants.timeFormat).format(time);
+  }
+
+  static String formatDateTime(DateTime dateTime) {
+    return DateFormat(AppConstants.dateTimeFormat).format(dateTime);
+  }
+
+  static String formatRelativeDate(DateTime date) {
+    final now = DateTime.now();
+    final difference = now.difference(date);
+
+    if (difference.inDays == 0) {
+      if (difference.inHours == 0) {
+        if (difference.inMinutes == 0) {
+          return 'Just now';
+        }
+        return '${difference.inMinutes}m ago';
+      }
+      return '${difference.inHours}h ago';
+    } else if (difference.inDays == 1) {
+      return 'Yesterday';
+    } else if (difference.inDays < 7) {
+      return '${difference.inDays}d ago';
+    } else {
+      return formatDate(date);
+    }
+  }
+
+  // Currency Formatting
+  static String formatCurrency(double amount, {String symbol = '\$'}) {
+    final formatter = NumberFormat.currency(symbol: symbol, decimalDigits: 2);
+    return formatter.format(amount);
+  }
+
+  static String formatBirr(double amount) {
+    return formatCurrency(amount, symbol: 'ETB ');
+  }
+
+  // Weight Formatting
+  static String formatWeight(double weightKg) {
+    if (weightKg < 1) {
+      return '${(weightKg * 1000).toStringAsFixed(0)} g';
+    }
+    return '${weightKg.toStringAsFixed(1)} kg';
+  }
+
+  // Status Colors
+  static Color getStatusColor(String status) {
+    switch (status.toLowerCase()) {
+      case 'pending':
+        return AppColors.statusPending;
+      case 'active':
+        return AppColors.statusActive;
+      case 'confirmed':
+        return AppColors.statusConfirmed;
+      case 'completed':
+        return AppColors.statusCompleted;
+      case 'cancelled':
+        return AppColors.statusCancelled;
+      default:
+        return AppColors.grey500;
+    }
+  }
+
+  // Snackbar Helpers
+  static void showSnackBar(
+    BuildContext context,
+    String message, {
+    bool isError = false,
+    Duration duration = const Duration(seconds: 3),
+  }) {
+    ScaffoldMessenger.of(context).showSnackBar(
+      SnackBar(
+        content: Text(message),
+        backgroundColor: isError ? AppColors.error : null,
+        duration: duration,
+        action: SnackBarAction(
+          label: 'Dismiss',
+          textColor: AppColors.white,
+          onPressed: () {
+            ScaffoldMessenger.of(context).hideCurrentSnackBar();
+          },
+        ),
+      ),
+    );
+  }
+
+  static void showSuccessSnackBar(BuildContext context, String message) {
+    ScaffoldMessenger.of(context).showSnackBar(
+      SnackBar(
+        content: Row(
+          children: [
+            const Icon(Icons.check_circle, color: AppColors.white),
+            const SizedBox(width: 12),
+            Expanded(child: Text(message)),
+          ],
+        ),
+        backgroundColor: AppColors.success,
+      ),
+    );
+  }
+
+  static void showErrorSnackBar(BuildContext context, String message) {
+    ScaffoldMessenger.of(context).showSnackBar(
+      SnackBar(
+        content: Row(
+          children: [
+            const Icon(Icons.error, color: AppColors.white),
+            const SizedBox(width: 12),
+            Expanded(child: Text(message)),
+          ],
+        ),
+        backgroundColor: AppColors.error,
+      ),
+    );
+  }
+
+  // Dialog Helpers
+  static Future<bool?> showConfirmDialog(
+    BuildContext context, {
+    required String title,
+    required String message,
+    String confirmText = 'Confirm',
+    String cancelText = 'Cancel',
+    bool isDestructive = false,
+  }) {
+    return showDialog<bool>(
+      context: context,
+      builder: (context) => AlertDialog(
+        title: Text(title),
+        content: Text(message),
+        actions: [
+          TextButton(
+            onPressed: () => Navigator.of(context).pop(false),
+            child: Text(cancelText),
+          ),
+          ElevatedButton(
+            onPressed: () => Navigator.of(context).pop(true),
+            style: isDestructive
+                ? ElevatedButton.styleFrom(backgroundColor: AppColors.error)
+                : null,
+            child: Text(confirmText),
+          ),
+        ],
+      ),
+    );
+  }
+
+  // Loading Dialog
+  static void showLoadingDialog(BuildContext context, {String? message}) {
+    showDialog(
+      context: context,
+      barrierDismissible: false,
+      builder: (context) => WillPopScope(
+        onWillPop: () async => false,
+        child: AlertDialog(
+          content: Row(
+            children: [
+              const CircularProgressIndicator(),
+              const SizedBox(width: 20),
+              Expanded(child: Text(message ?? 'Please wait...')),
+            ],
+          ),
+        ),
+      ),
+    );
+  }
+
+  static void hideLoadingDialog(BuildContext context) {
+    Navigator.of(context).pop();
+  }
+
+  // Name Initials
+  static String getInitials(String name) {
+    final names = name.trim().split(' ');
+    if (names.isEmpty) return '';
+    if (names.length == 1) return names[0][0].toUpperCase();
+    return '${names[0][0]}${names[names.length - 1][0]}'.toUpperCase();
+  }
+
+  // Truncate Text
+  static String truncateText(String text, int maxLength) {
+    if (text.length <= maxLength) return text;
+    return '${text.substring(0, maxLength)}...';
+  }
+}
